mapper.select:
        select 

	    from t_sys_user
	    where LOGIN_NAME = #{loginName,jdbcType=VARCHAR} and PWD = #{pwd,jdbcType=VARCHAR}

select LOGIN_NAME, DISPLAY_NAME, ug.GROUP_NAME GROUP_ID, STATUS, CREATE_AT, CREATE_BY, CREATE_BY_TXT, LAST_MODIFIED, LAST_MODIFIED_BY, LAST_MODIFIED_BY_TXT, BRANCH_NO, DEALER_ID, SALES_ORG, LAST_LOGIN from T_SYS_USER user,T_SYS_USER_GROUP ug where user.GROUP_ID=ug.ID
and (user.LOGIN_NAME like '%' #{uname} '%' or user.DISPLAY_NAME like '%' #{uname} '%')
and ug.GROUP_NAME like '%' #{userGroupName} '%'
and user.CREATE_AT > #{startDate}
and user.CREATE_AT < #{endDate}
order by user.CREATE_AT desc,user.LAST_MODIFIED desc
