mapper.update:
update t_md_branch_emp set  LAST_MODIFIED = #{lastModified,jdbcType=TIMESTAMP},
  LAST_MODIFIED_BY = #{lastModifiedBy,jdbcType=VARCHAR},
  LAST_MODIFIED_BY_TXT = #{lastModifiedByTxt,jdbcType=VARCHAR},
  STATUS = #{status}
where EMP_NO = #{empNo}

update t_md_emp_role
set
    ROLE_ID = #{roleId,jdbcType=VARCHAR}
where EMP_NO = #{empNo,jdbcType=VARCHAR}

mapper.select:
  select emp.EMP_NO, emp.HR_EMP_NO, branch.BRANCH_NAME  BRANCH_NO, role.ROLE_NAME roleId,emp.EMP_NAME,emp.GENDER,
emp.EMP_TYPE, emp.TEL, emp.MP, emp.WEBCHAT, emp.ID_NO,emp.STATUS
from t_md_branch_emp emp
LEFT JOIN  T_MD_BRANCH branch on emp.BRANCH_NO = branch.BRANCH_NO

LEFT JOIN T_MD_EMP_ROLE erole on emp.EMP_NO = erole.EMP_NO
LEFT JOIN t_sys_role role  on  erole.ROLE_ID = role.ID
where  1=1
AND emp.SALES_ORG = #{salesOrg,jdbcType=VARCHAR}

 		AND emp.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}


 	AND  emp.STATUS = #{status,jdbcType=VARCHAR}



  AND role.ID = #{roleId,jdbcType=VARCHAR}

select e.EMP_NO, e.HR_EMP_NO, e.BRANCH_NO, e.EMP_NAME,e.GENDER, e.EMP_TYPE, e.TEL,e.MP, e.WEBCHAT, e.ID_NO, e.CREATE_AT,e.SALES_ORG, e.CREATE_BY, e.CREATE_BY_TXT, e.LAST_MODIFIED, e.LAST_MODIFIED_BY, e.LAST_MODIFIED_BY_TXT,e.SALARY_MET,e.BASE_SALARY,e.JOIN_DATE, e.STATUS from T_MD_BRANCH_EMP e LEFT JOIN t_sys_user_role u on e.EMP_NO = u.LOGIN_NAME where 1=1 and u.ID=
AND BRANCH_NO = #{branchNo,jdbcType=VARCHAR}
AND SALES_ORG = #{salesOrg,jdbcType=VARCHAR}

select
e.EMP_NO,e.EMP_NAME
from T_MD_BRANCH_EMP e LEFT JOIN t_sys_user_role u on e.EMP_NO = u.LOGIN_NAME
LEFT  JOIN  t_sys_role r on u.ID = r.ID
where 1=1
AND  e.SALES_ORG = #{salesOrg,jdbcType=VARCHAR}

   and u.ID=  


  AND  e.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}


  AND  e.DEALER_NO = #{dealerNo,jdbcType=VARCHAR}

GROUP BY e.EMP_NO
