mapper.update:
update t_md_branch_emp set  LAST_MODIFIED = #{lastModified,jdbcType=TIMESTAMP},
  LAST_MODIFIED_BY = #{lastModifiedBy,jdbcType=VARCHAR},
  LAST_MODIFIED_BY_TXT = #{lastModifiedByTxt,jdbcType=VARCHAR},
  STATUS = #{status}
where EMP_NO = #{empNo}

update t_md_emp_role
set
    ROLE_ID = #{roleId,jdbcType=VARCHAR}
where EMP_NO = #{empNo,jdbcType=VARCHAR}

mapper.select:
  select emp.EMP_NO, emp.HR_EMP_NO, branch.BRANCH_NAME  BRANCH_NO, role.ROLE_NAME roleId,emp.EMP_NAME,emp.GENDER, emp.EMP_TYPE, emp.TEL, emp.MP, emp.WEBCHAT, emp.ID_NO,emp.STATUS
from t_md_branch_emp emp,T_MD_BRANCH branch,T_MD_EMP_ROLE erole,t_sys_role role
where emp.BRANCH_NO = branch.BRANCH_NO and erole.ROLE_ID = role.ID and emp.EMP_NO = erole.EMP_NO

 		AND emp.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}


 	AND  emp.STATUS = #{status,jdbcType=VARCHAR}



  AND role.ID = #{roleId,jdbcType=VARCHAR}
