mapper.select:
select
p.MATNR, p.MATNR_TXT, p.BASE_UNIT, p.STATUS, p.LAST_MODIFIED, p.CREATE_AT, p.FIRST_CAT, p.SEC_CATE,
p.BRAND, p.WEIGHT, p.WEIGHT_UNIT, p.IMPORTANT_PRD_FLAG, p.SPEC, p.SPEC_UNIT, p.BOT_SPEC, p.ZBOT_CODE,
pex.SHORT_TXT, pex.PRE_DAYS, pex.BOT_TYPE
FROM t_md_mara p, t_md_mara_ex pex
WHERE p.MATNR = pex.MATNR

    AND p.BRAND = #{brandLib,jdbcType=VARCHAR}


    AND p.STATUS = #{status,jdbcType=VARCHAR}


    AND p.BASE_UNIT = #{catalog,jdbcType=VARCHAR}

ORDER BY p.MATNR

mapper.update:
update t_md_mara
set STATUS = "æ˜¯"
where MATNR = #{0}
