mapper.select:
select b.BRANCH_NO BRANCH_NO,b.BRANCH_NAME BRANCH_NAME,o.ORDER_NO ORDER_NO, o.PREORDER_SOURCE PREORDER_SOURCE,o.PAYMENTMETHOD PAYMENT_TYPE, o.INIT_AMT SATTLEMENT_PRICE,cust.VIP_CUST_NO VIP_CUST_NO,cust.VIP_NAME VIP_Name, cust.MP VIP_MP,cust.TEL VIP_TEL, cust.ADDRESS_TXT ADDRESS, emp.EMP_NO EMP_NO, emp.EMP_NAME EMP_NAME,o.PAYMENT_STAT PAYMENT_STAT,bill.RECEIPT_DATE, ( CASE when o.PAYMENT_STAT ='20' then bill.AMT else '0' END) REAL_COLLECTION, ( CASE when o.PAYMENT_STAT ='20' then bill.PAYMENT_YEAR_MONTH else 'æ— ' END) PAYMENT_DATE FROM t_mst_recv_bill bill LEFT JOIN t_preorder o ON bill.ORDER_NO = o.ORDER_NO LEFT JOIN t_md_branch_emp emp on o.EMP_NO = emp.EMP_NO LEFT JOIN t_vip_cust_info cust on o.MILKMEMBER_NO = cust.VIP_CUST_NO LEFT JOIN t_md_branch b on o.BRANCH_NO = b.BRANCH_NO WHERE 1=1 and o.BRANCH_NO is not null
and o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}
and o.END_DATE >= #{startDate,jdbcType=DATE}
and o.END_DATE <= #{endDate,jdbcType=DATE}

SELECT
    SUM(t1.QTY) QTY,SUM(t1.PRICE * t1.QTY) SALE_PRICE,t1.DISP_DATE,t1.DISP_EMP_NO,e.EMP_NO,e.EMP_NAME
FROM (
        SELECT
        i.MATNR,i.QTY,i.PRICE,o.DISP_EMP_NO,o.DISP_DATE
        FROM t_mst_inside_sal_order_item i LEFT JOIN t_mst_inside_sal_order so on i.INS_ORDER_NO = so.INS_ORDER_NO
        LEFT JOIN t_mst_disp_order o on so.DISP_ORDER_NO = o.ORDER_NO
        WHERE 1= 1

            and   o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}


            and  o.DISP_DATE >= #{startDate,jdbcType=DATE}


            and  o.DISP_DATE <= #{endDate,jdbcType=DATE}

        UNION ALL


        SELECT
        i.MATNR,i.QTY ,i.PRICE ,o.DISP_EMP_NO,o.DISP_DATE
        FROM t_mst_disp_order_item i LEFT JOIN t_mst_disp_order o ON i.ORDER_NO = o.ORDER_NO
        WHERE 1= 1

            and o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}


            and o.DISP_DATE >= #{startDate,jdbcType=DATE}


            and o.DISP_DATE <= #{endDate,jdbcType=DATE}


) t1 LEFT  JOIN  t_md_branch_emp e on t1.DISP_EMP_NO = e.EMP_NO
GROUP BY t1.DISP_DATE,t1.DISP_EMP_NO

SELECT
i.MATNR,a.MATNR_TXT,i.QTY,i.PRICE,o.DISP_EMP_NO
FROM t_mst_inside_sal_order_item i LEFT JOIN t_mst_inside_sal_order so on i.INS_ORDER_NO = so.INS_ORDER_NO
LEFT JOIN t_mst_disp_order o on so.DISP_ORDER_NO = o.ORDER_NO
LEFT JOIN t_md_mara a on (i.MATNR = a.MATNR and a.SALES_ORG = #{salesOrg,jdbcType=VARCHAR})
WHERE 1=1

      and so.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}

   and  o.DISP_DATE = #{dispDate,jdbcType=DATE}
   and  o.DISP_EMP_NO = #{empNo,jdbcType=VARCHAR}

UNION ALL

SELECT
i.MATNR,a.MATNR_TXT,i.QTY ,i.PRICE ,o.DISP_EMP_NO
FROM t_mst_disp_order_item i LEFT JOIN t_mst_disp_order o ON i.ORDER_NO = o.ORDER_NO
LEFT JOIN t_md_mara a on (i.MATNR = a.MATNR and a.SALES_ORG = #{salesOrg,jdbcType=VARCHAR})
WHERE 1=1

    and o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}

  and  o.DISP_DATE = #{dispDate,jdbcType=DATE}
 and  o.DISP_EMP_NO = #{empNo,jdbcType=VARCHAR}
