SELECT
di.DISP_DATE,o.EMP_NO,e.EMP_NAME,i.MATNR,m.MATNR_TXT,SUM(i.QTY) QTY,o.BRANCH_NO,b.BRANCH_NAME,i.INS_ORDER_NO ORDER_NO,b.DEALER_NO,d.DEALER_NAME,
(select '内部销售订单产品' from DUAL) TYPE
FROM t_mst_inside_sal_order_item i
LEFT JOIN t_mst_order_daliy_plan_item di on i.ORG_ORDER_NO = di.ORDER_NO
LEFT JOIN t_preorder o on i.ORG_ORDER_NO = o.ORDER_NO
LEFT JOIN t_md_branch b on o.BRANCH_NO = b.BRANCH_NO
LEFT JOIN t_md_dealer d on b.DEALER_NO = d.DEALER_NO
LEFT JOIN t_md_branch_emp e on o.EMP_NO = e.EMP_NO and e.SALES_ORG =#{salesOrg,jdbcType=VARCHAR}
LEFT JOIN t_md_mara  m on (i.MATNR = m.MATNR and m.SALES_ORG=#{salesOrg,jdbcType=VARCHAR})
where 1=1

    AND  b.DEALER_NO = #{dealerNo,jdbcType=VARCHAR}


    AND  o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}


    AND  o.EMP_NO = #{empNo,jdbcType=VARCHAR}


    AND di.DISP_DATE >= #{startDate,jdbcType=DATE}


    AND  di.DISP_DATE <= #{endDate,jdbcType=DATE}

GROUP BY  di.DISP_DATE,o.EMP_NO,i.MATNR
UNION ALL

SELECT
i.DISP_DATE,o.EMP_NO,e.EMP_NAME,i.MATNR,m.MATNR_TXT,SUM(i.QTY) QTY,o.BRANCH_NO,b.BRANCH_NAME,i.ORDER_NO,b.DEALER_NO,d.DEALER_NAME,
(select '配送产品' from DUAL) TYPE
FROM t_mst_order_daliy_plan_item i
LEFT JOIN t_preorder o on i.ORDER_NO = o.ORDER_NO
LEFT JOIN t_md_mara m on (i.MATNR = m.MATNR and m.SALES_ORG=  #{salesOrg,jdbcType=VARCHAR})
LEFT JOIN t_md_branch b on o.BRANCH_NO = b.BRANCH_NO
LEFT JOIN t_md_dealer d on b.DEALER_NO = d.DEALER_NO
LEFT JOIN t_md_branch_emp e on o.EMP_NO= e.EMP_NO and e.SALES_ORG = #{salesOrg,jdbcType=VARCHAR}
where i.`STATUS` !='30'
and i.GIFT_QTY IS NULL
and o.SALES_ORG = #{salesOrg,jdbcType=VARCHAR}

    AND  b.DEALER_NO = #{dealerNo,jdbcType=VARCHAR}


    AND o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}


    AND  o.EMP_NO = #{empNo,jdbcType=VARCHAR}


    AND   i.DISP_DATE >= #{startDate,jdbcType=DATE}


    AND  i.DISP_DATE <= #{endDate,jdbcType=DATE}

GROUP BY i.DISP_DATE,o.EMP_NO,i.MATNR
UNION ALL

SELECT
i.DISP_DATE,o.EMP_NO,e.EMP_NAME,i.MATNR,m.MATNR_TXT,SUM(i.GIFT_QTY) QTY,o.BRANCH_NO,b.BRANCH_NAME,i.ORDER_NO,b.DEALER_NO,d.DEALER_NAME,
(select '赠送产品' from DUAL) TYPE
FROM t_mst_order_daliy_plan_item i
LEFT JOIN t_preorder o on i.ORDER_NO = o.ORDER_NO
LEFT JOIN t_md_mara m on (i.MATNR = m.MATNR and m.SALES_ORG=  #{salesOrg,jdbcType=VARCHAR})
LEFT JOIN t_md_branch b on o.BRANCH_NO = b.BRANCH_NO
LEFT JOIN t_md_dealer d on b.DEALER_NO = d.DEALER_NO
LEFT JOIN t_md_branch_emp e on o.EMP_NO= e.EMP_NO and e.SALES_ORG = #{salesOrg,jdbcType=VARCHAR}
where i.`STATUS` !='30'
and i.GIFT_QTY IS NOT NULL
and o.SALES_ORG = #{salesOrg,jdbcType=VARCHAR}

    AND  b.DEALER_NO = #{dealerNo,jdbcType=VARCHAR}


    AND o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}


    AND  o.EMP_NO = #{empNo,jdbcType=VARCHAR}


    AND   i.DISP_DATE >= #{startDate,jdbcType=DATE}


    AND  i.DISP_DATE <= #{endDate,jdbcType=DATE}

GROUP BY i.DISP_DATE,o.EMP_NO,i.MATNR
ORDER BY  DISP_DATE DESC,TYPE

SELECT o.ORDER_NO, o.PAYMENTMETHOD, e.EMP_NAME, c.VIP_NAME CUST_NAME, br.BRANCH_NAME, a.ACCT_AMT, c.MP, c.ADDRESS_TXT ADDRESS, o.INIT_AMT ACCOUNT_RECE, b.AMT ACCOUNT_REAL, (o.INIT_AMT-b.AMT) ACCOUNT_GAP, ( SELECT i.ITEM_NAME from t_sys_code_item i WHERE i.TYPE_CODE= and i.ITEM_CODE = b.PAYMENT_TYPE ) PAYMENT_TYPE, b.RECEIPT_DATE PAYMENT_DATE FROM t_mst_recv_bill b LEFT JOIN t_preorder o on o.ORDER_NO = b.ORDER_NO LEFT JOIN t_md_branch_emp e on o.EMP_NO = e.EMP_NO LEFT JOIN t_vip_cust_info c on o.MILKMEMBER_NO = c.VIP_CUST_NO LEFT JOIN t_vip_acct a on c.VIP_CUST_NO = a.VIP_CUST_NO LEFT JOIN t_md_branch br on o.BRANCH_NO = br.BRANCH_NO WHERE 1= 1
and o.SALES_ORG = #{salesOrg,jdbcType=VARCHAR}
and o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}
and o.EMP_NO = #{empNo,jdbcType=VARCHAR}
and b.RECEIPT_DATE >= #{startDate,jdbcType=DATE}
and b.RECEIPT_DATE <= #{endDate,jdbcType=DATE}

select
    e.BRANCH_NO,b.BRANCH_NAME,e.EMP_NO,e.EMP_NAME,e.BASE_SALARY,SALARY_MET
from t_md_branch_emp e LEFT JOIN t_md_branch b on e.BRANCH_NO = b.BRANCH_NO
where 1=1

        and e.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}



            and e. EMP_NO = #{empNo,jdbcType=VARCHAR}

SELECT t.MATNR,SUM(t.QTY) QTY
FROM
(
SELECT  MATNR,QTY
FROM  t_mst_order_daliy_plan_item  i LEFT JOIN t_preorder o on i.ORDER_NO = o.ORDER_NO
WHERE 1 =1

        and o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}


        and  o.EMP_NO = #{empNo,jdbcType=VARCHAR}


        and  o.END_DATE >= #{startDate,jdbcType=DATE}


        and  o.END_DATE <= #{endDate,jdbcType=DATE}

UNION  ALL

SELECT MATNR,QTY
    FROM  t_mst_inside_sal_order_item s LEFT JOIN t_preorder o on s.ORG_ORDER_NO = o.ORDER_NO
    WHERE 1 =1

        and o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}


        and  o.EMP_NO = #{empNo,jdbcType=VARCHAR}


        and  o.END_DATE >= #{startDate,jdbcType=DATE}


        and  o.END_DATE <= #{endDate,jdbcType=DATE}

) t
GROUP BY MATNR

SELECT SUM(t.QTY) QTY
FROM
(
    SELECT SUM(QTY) QTY
    FROM  t_mst_order_daliy_plan_item  i LEFT JOIN t_preorder o on i.ORDER_NO = o.ORDER_NO
    WHERE 1 =1

                and   o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}


                and  o.EMP_NO = #{empNo,jdbcType=VARCHAR}


                and  o.END_DATE >= #{startDate,jdbcType=DATE}


                and  o.END_DATE <= #{endDate,jdbcType=DATE}


UNION  ALL

    SELECT SUM(QTY) QTY
    FROM  t_mst_inside_sal_order_item s LEFT JOIN t_preorder o on s.ORG_ORDER_NO = o.ORDER_NO
    WHERE 1 =1

            and   o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}


            and  o.EMP_NO = #{empNo,jdbcType=VARCHAR}


            and  o.END_DATE >= #{startDate,jdbcType=DATE}


            and  o.END_DATE <= #{endDate,jdbcType=DATE}

) t

