mapper.select:
SELECT
di.DISP_DATE,i.MATNR,i.QTY,o.BRANCH_NO,o.EMP_NO,(select '内部销售订单产品' from DUAL) TYPE
FROM t_mst_inside_sal_order_item i LEFT JOIN t_mst_order_daliy_plan_item di on i.ORG_ORDER_NO = di.ORDER_NO
LEFT JOIN t_preorder o on i.ORG_ORDER_NO = o.ORDER_NO
where 1=1

    AND  o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}


    AND  o.EMP_NO = #{empNo,jdbcType=VARCHAR}


    AND di.DISP_DATE >= #{startDate,jdbcType=DATE}


    AND  di.DISP_DATE <= #{endDate,jdbcType=DATE}


UNION ALL

SELECT
i.DISP_DATE,i.MATNR,i.QTY,o.BRANCH_NO,o.EMP_NO,(select '配送产品' from DUAL) TYPE
FROM t_mst_order_daliy_plan_item i LEFT JOIN t_preorder o on i.ORDER_NO = o.ORDER_NO
where 1=1

    AND o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}


    AND  o.EMP_NO = #{empNo,jdbcType=VARCHAR}


    AND   i.DISP_DATE >= #{startDate,jdbcType=DATE}


    AND  i.DISP_DATE <= #{endDate,jdbcType=DATE}

ORDER BY  DISP_DATE ASC,TYPE

        SELECT
        o.ORDER_NO,e.EMP_NAME,c.VIP_NAME CUST_NAME,c.MP,c.ADDRESS_TXT ADDRESS,o.INIT_AMT ACCOUNT_RECE,b.AMT ACCOUNT_REAL,
        (o.INIT_AMT-b.AMT) ACCOUNT_GAP,b.PAYMENT_TYPE,b.PAYMENT_YEAR_MONTH PAYMENT_DATE
        FROM  t_preorder o LEFT JOIN t_md_branch_emp e on o.EMP_NO = e.EMP_NO
						    LEFT JOIN t_vip_cust_info c on o.MILKMEMBER_NO = c.VIP_CUST_NO
						    LEFT JOIN t_mst_recv_bill b on o.ORDER_NO = b.ORDER_NO
        WHERE 1= 1

                and o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}


               and  o.EMP_NO = #{empNo,jdbcType=VARCHAR}


               and  o.END_DATE >= #{startDate,jdbcType=DATE}


               and  o.END_DATE <= #{endDate,jdbcType=DATE}

select
    e.BRANCH_NO,b.BRANCH_NAME,e.EMP_NO,e.EMP_NAME,e.BASE_SALARY,SALARY_MET
from t_md_branch_emp e LEFT JOIN t_md_branch b on e.BRANCH_NO = b.BRANCH_NO
where 1=1

        and e.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}



            and e. EMP_NO = #{empNo,jdbcType=VARCHAR}

SELECT t.MATNR,SUM(t.QTY) QTY
FROM
(
SELECT  MATNR,QTY
FROM  t_mst_order_daliy_plan_item  i LEFT JOIN t_preorder o on i.ORDER_NO = o.ORDER_NO

        and o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}


        and  o.EMP_NO = #{empNo,jdbcType=VARCHAR}


        and  o.END_DATE >= #{startDate,jdbcType=DATE}


        and  o.END_DATE <= #{endDate,jdbcType=DATE}

UNION  ALL

SELECT MATNR,QTY
    FROM  t_mst_inside_sal_order_item s LEFT JOIN t_preorder o on s.ORG_ORDER_NO = o.ORDER_NO

        and o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}


        and  o.EMP_NO = #{empNo,jdbcType=VARCHAR}


        and  o.END_DATE >= #{startDate,jdbcType=DATE}


        and  o.END_DATE <= #{endDate,jdbcType=DATE}

) t
GROUP BY MATNR

SELECT SUM(i.QTY)+SUM(s.QTY) QTY

FROM  t_mst_order_daliy_plan_item  i
      LEFT JOIN t_mst_inside_sal_order_item s on s.MATNR = i.MATNR
      LEFT JOIN t_preorder o on i.ORDER_NO = o.ORDER_NO

    and   o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}


    and  o.EMP_NO = #{empNo,jdbcType=VARCHAR}


    and  o.END_DATE >= #{startDate,jdbcType=DATE}


    and  o.END_DATE <= #{endDate,jdbcType=DATE}
