mapper.select:
select * from ( (select ORDER_NO ORDER_NO1,ORDER_DATE ORDER_DATE1,ITEM_NO ITEM_NO1,CONFIRM_MATNR CONFIRM_MATNR1, MATNR MATNR1,CONFIRM_QTY CONFIRM_QTY1,QTY QTY1,UNIT UNIT1,PRICE PRICE1, ADDRESS_NO ADDRESS_NO1,ORG_ITEM_NO ORG_ITEM_NO1,ORG_ORDER_NO ORG_ORDER_NO1, REACH_TIME_TYPE REACH_TIME_TYPE1, DISP_EMP_NO DISP_EMP_NO1 from t_mst_disp_order_item a where a.order_date = #{itemNo,jdbcType=DATE} and a.GIFT_FLAG is null
and a.ORDER_NO = #{addressNo,jdbcType=VARCHAR}
) a1 left outer join (select ORDER_NO ORDER_NO2,ORDER_DATE ORDER_DATE2,ITEM_NO ITEM_NO2,CONFIRM_MATNR CONFIRM_MATNR2, MATNR MATNR2,CONFIRM_QTY CONFIRM_QTY2,QTY QTY2,UNIT UNIT2,PRICE PRICE2, ADDRESS_NO ADDRESS_NO2,ORG_ITEM_NO ORG_ITEM_NO2,ORG_ORDER_NO ORG_ORDER_NO2, REACH_TIME_TYPE REACH_TIME_TYPE2, DISP_EMP_NO DISP_EMP_NO2 from t_mst_disp_order_item b where b.order_date = #{orderNo,jdbcType=DATE} and b.GIFT_FLAG is null
and b.ORDER_NO = #{addressNo,jdbcType=VARCHAR}
) b1 on a1.org_order_no1 = b1.org_order_no2 and a1.org_item_no1 = b1.org_item_no2 ) where (a1.matnr1 != b1.matnr2 or a1.matnr1 is null or b1.matnr2 is null) or (a1.qty1 != b1.qty2 or a1.qty1 is null or b1.qty2 is null) or (a1.reach_time_type1 != b1.reach_time_type2 or a1.reach_time_type1 is null or b1.reach_time_type2 is null)
union
select * from ( (select ORDER_NO ORDER_NO1,ORDER_DATE ORDER_DATE1,ITEM_NO ITEM_NO1,CONFIRM_MATNR CONFIRM_MATNR1, MATNR MATNR1,CONFIRM_QTY CONFIRM_QTY1,QTY QTY1,UNIT UNIT1,PRICE PRICE1, ADDRESS_NO ADDRESS_NO1,ORG_ITEM_NO ORG_ITEM_NO1,ORG_ORDER_NO ORG_ORDER_NO1, REACH_TIME_TYPE REACH_TIME_TYPE1, DISP_EMP_NO DISP_EMP_NO1 from t_mst_disp_order_item a where a.order_date = #{itemNo,jdbcType=DATE} and a.GIFT_FLAG is null
and a.ORDER_NO = #{addressNo,jdbcType=VARCHAR}
) a1 right outer join (select ORDER_NO ORDER_NO2,ORDER_DATE ORDER_DATE2,ITEM_NO ITEM_NO2,CONFIRM_MATNR CONFIRM_MATNR2, MATNR MATNR2,CONFIRM_QTY CONFIRM_QTY2,QTY QTY2,UNIT UNIT2,PRICE PRICE2, ADDRESS_NO ADDRESS_NO2,ORG_ITEM_NO ORG_ITEM_NO2,ORG_ORDER_NO ORG_ORDER_NO2, REACH_TIME_TYPE REACH_TIME_TYPE2, DISP_EMP_NO DISP_EMP_NO2 from t_mst_disp_order_item b where b.order_date = #{orderNo,jdbcType=DATE} and b.GIFT_FLAG is null
and b.ORDER_NO = #{addressNo,jdbcType=VARCHAR}
) b1 on a1.org_order_no1 = b1.org_order_no2 and a1.org_item_no1 = b1.org_item_no2 ) where (a1.matnr1 != b1.matnr2 or a1.matnr1 is null or b1.matnr2 is null) or (a1.qty1 != b1.qty2 or a1.qty1 is null or b1.qty2 is null) or (a1.reach_time_type1 != b1.reach_time_type2 or a1.reach_time_type1 is null or b1.reach_time_type2 is null)

select 

from t_mst_disp_order_item
where ORG_ORDER_NO = #{orderNo,jdbcType=VARCHAR}
      and ORG_ITEM_NO = #{itemNo,jdbcType=VARCHAR}
      and ORDER_DATE = #{orderDate,jdbcType=DATE}

select 

from t_mst_disp_order_item
where i.ORDER_NO = #{orderNo,jdbcType=VARCHAR}
  and i.ITEM_NO = #{itemNo,jdbcType=VARCHAR}

  and i.ORDER_DATE = #{orderDate,jdbcType=DATE}

mapper.delete:
delete from t_mst_disp_order_item
where ORDER_NO = #{orderNo,jdbcType=VARCHAR}
  and ORDER_DATE = #{orderDate,jdbcType=DATE}
  and ITEM_NO = #{itemNo,jdbcType=VARCHAR}

mapper.select:
select i.ORDER_NO, i.ORDER_DATE, i.ITEM_NO,i.CONFIRM_MATNR, i.MATNR, i.CONFIRM_QTY, i.QTY, i.UNIT, i.PRICE, i.AMT, i.CONFIRM_AMT, i.STATUS, i.PICKUP_ORDER_NO, i.PICKUP_ORDER_ITEM_NO, i.REASON, i.REDISP_DATE, i.WHOLESALE_PRICE, i.WHOLESALE_PRICE_1, i.ADDRESS_NO,i.ORG_ITEM_NO,i.ORG_ORDER_NO,i.REACH_TIME_TYPE,p.SHORT_TXT MATNR_TXT, concat(r.RESIDENTIAL_AREA_TXT,a.ADDRESS_TXT) ADDRESS_TXT from t_mst_disp_order_item i left join t_preorder o on o.ORDER_NO = i.ORG_ORDER_NO left join t_md_mara_ex p on p.MATNR = i.MATNR and p.SALES_ORG = o.SALES_ORG left join t_md_address a on a.ADDRESS_ID = i.ADDRESS_NO left join t_md_residential_area r on r.ID = a.RESIDENTIAL_AREA where i.ORDER_NO = #{orderNo,jdbcType=VARCHAR}
and i.ORDER_DATE = #{orderDate,jdbcType=DATE}
and i.ITEM_NO = #{itemNo,jdbcType=VARCHAR}

select
o.BRANCH_NO,o.DISP_EMP_NO EMP_NO,i.ORDER_NO,i.ORDER_DATE,i.MATNR,i.QTY,i.UNIT,i.PRICE,i.CONFIRM_QTY,i.AMT,i.STATUS,i.REASON
from t_mst_disp_order_item  i,t_mst_disp_order o
where i.ORDER_NO = o.ORDER_NO
  and ( i.REASON is not null  and i.REASON <> '30')

    and o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}


    and o.DISP_EMP_NO = #{empNo,jdbcType=VARCHAR}



    and o.CREAT_AT = #{createDate,jdbcType=DATE}


    and o.ORDER_NO = #{orderNo,jdbcType=VARCHAR}
