mapper.select:
select 

from t_mst_milkbox_plan
where 1 = 1

 		AND MILKBOX_STAT = #{status,jdbcType=VARCHAR}


 		AND EMP_NO = #{emp,jdbcType=VARCHAR}


 		AND PLAN_DATE >= #{setDateStart,jdbcType=DATE}


 		AND PLAN_DATE <= #{setDateEnd,jdbcType=DATE}

mapper.update:
update t_mst_milkbox_plan
set PRINT_FLAG = "20"
where PLAN_NO = #{planNo,jdbcType=VARCHAR}
