mapper.select:
select 

from t_mst_order_daliy_plan_item
where 1=1
and ORDER_NO = #{orderNo,jdbcType=VARCHAR}
and DISP_DATE >= #{orderDateStart,jdbcType=DATE}
and STATUS = '30'

select 

from t_mst_order_daliy_plan_item
where ORDER_NO = #{orderNo,jdbcType=VARCHAR}
  and ORDER_DATE = #{orderDate,jdbcType=DATE}
  and PLAN_ITEM_NO = #{planItemNo,jdbcType=VARCHAR}
  and ITEM_NO = #{itemNo,jdbcType=VARCHAR}

select 

from t_mst_order_daliy_plan_item
where 1 = 1 
  and ITEM_NO = #{itemNo,jdbcType=VARCHAR}
  and PLAN_ITEM_NO = #{planItemNo,jdbcType=VARCHAR}

select max(cast(PLAN_ITEM_NO as decimal(10,0)))
from t_mst_order_daliy_plan_item 
where ORDER_NO = #{orderNo,jdbcType=VARCHAR}

    select max(cast(REMAIN_AMT as decimal(10,2)))
    from t_mst_order_daliy_plan_item 
    where ORDER_NO = #{orderNo,jdbcType=VARCHAR}
			 and DISP_DATE >= #{itemNo,jdbcType=VARCHAR}

    select
    d.ORDER_NO, d.ORDER_DATE, d.PLAN_ITEM_NO, d.ITEM_NO, d.DISP_DATE, d.REACH_TIME, d.REACH_TIME_TYPE, 
    d.MATNR, d.UNIT, d.QTY, d.PRICE, d.AMT, d.REMAIN_AMT, d.STATUS, d.CREATE_AT, d.CREATE_BY, o.ADRESS_NO CREATE_BY_TXT, 
    d.LAST_MODIFIED, d.LAST_MODIFIED_BY,o.emp_no LAST_MODIFIED_BY_TXT 
	 from t_mst_order_daliy_plan_item d
	 left join t_preorder o on o.order_no = d.order_no
	 where d.disp_date = #{planItemNo,jdbcType=DATE}
	 and o.emp_no = #{itemNo,jdbcType=VARCHAR}
	 and d.reach_time_type = #{orderNo,jdbcType=VARCHAR}
	 and d.status != "30"

mapper.delete:
    delete from t_mst_order_daliy_plan_item
    where ORDER_NO = #{orderNo,jdbcType=VARCHAR}

	      and ITEM_NO = #{itemNo,jdbcType=VARCHAR}


	      and STATUS = '10'

      and DISP_DATE >= #{dispDateStr,jdbcType=DATE}

mapper.update:
    update t_mst_order_daliy_plan_item set STATUS = '10'
    where ORDER_NO = #{orderNo,jdbcType=VARCHAR}

	      and ITEM_NO = #{itemNo,jdbcType=VARCHAR}

      and DISP_DATE >= #{dispDateStr,jdbcType=DATE}

update t_mst_order_daliy_plan_item

    STATUS = "30"

where DISP_DATE >= #{stopDateStartStr,jdbcType=DATE}

    and DISP_DATE <= #{stopDateEndStr,jdbcType=DATE}

and ORDER_NO = #{orderNo,jdbcType=VARCHAR}

mapper.select:
select

from t_mst_order_daliy_plan_item
where
ORDER_NO = #{orderNo,jdbcType=VARCHAR}
and DISP_DATE = #{retReason,jdbcType=DATE}

		select i.ORDER_NO,i.ITEM_NO,i.DISP_DATE, i.MATNR,m.MATNR_TXT MATNR_TXT,i.QTY,m.BASE_UNIT UNIT
   from t_mst_order_daliy_plan_item i
     LEFT JOIN t_md_mara m on i.MATNR =  m.MATNR
     LEFT JOIN t_preorder o on 	i.ORDER_NO = o.ORDER_NO
     LEFT JOIN t_md_mara_ex e on i.MATNR = e.MATNR
     where   1=1
       and  e.PRE_DAYS=#{preDays,jdbcType=INTEGER}
       and m.SALES_ORG=#{salesOrg,jdbcType=VARCHAR}
       and o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}
       and i.DISP_DATE = #{requiredDate,jdbcType=DATE}
