mapper.select:
select 

from t_prom_scope_item
where PROM_NO = #{promNo,jdbcType=VARCHAR}
  and ITEM_NO = #{itemNo,jdbcType=VARCHAR}

mapper.delete:
delete from t_prom_scope_item
where PROM_NO = #{promNo,jdbcType=VARCHAR}
  and ITEM_NO = #{itemNo,jdbcType=VARCHAR}

mapper.update:
update t_prom_scope_item


    BRANCH_NO = #{branchNo,jdbcType=VARCHAR},


where PROM_NO = #{promNo,jdbcType=VARCHAR}
  and ITEM_NO = #{itemNo,jdbcType=VARCHAR}

update t_prom_scope_item
set BRANCH_NO = #{branchNo,jdbcType=VARCHAR}
where PROM_NO = #{promNo,jdbcType=VARCHAR}
  and ITEM_NO = #{itemNo,jdbcType=VARCHAR}

mapper.insert:
insert into t_prom_scope_item (PROM_NO, ITEM_NO, BRANCH_NO
  )
values (#{promNo,jdbcType=VARCHAR}, #{itemNo,jdbcType=VARCHAR}, #{branchNo,jdbcType=VARCHAR}
  )
