SELECT b.branch_no branchNo, br.BRANCH_NAME branchName, br.branch_group branchGroup, max( CASE b.ORDER_DATE WHEN date_format( DATE_ADD(#{theDate,jdbcType=DATE}, INTERVAL - 4 DAY), '%Y-%m-%d' ) THEN qty ELSE 0 END ) AS 'date1', max( CASE b.ORDER_DATE WHEN date_format( DATE_ADD(#{theDate,jdbcType=DATE}, INTERVAL - 3 DAY), '%Y-%m-%d' ) THEN qty ELSE 0 END ) AS 'date2', max( CASE b.ORDER_DATE WHEN date_format( DATE_ADD(#{theDate,jdbcType=DATE}, INTERVAL - 2 DAY), '%Y-%m-%d' ) THEN qty ELSE 0 END ) AS 'date3', max( CASE b.ORDER_DATE WHEN date_format( DATE_ADD(#{theDate,jdbcType=DATE}, INTERVAL - 1 DAY), '%Y-%m-%d' ) THEN qty ELSE 0 END ) AS 'date4', max( CASE b.ORDER_DATE WHEN date_format(#{theDate,jdbcType=DATE}, '%Y-%m-%d') THEN qty ELSE 0 END ) AS 'date5', ROUND( (max( CASE b.ORDER_DATE WHEN date_format(#{theDate,jdbcType=DATE}, '%Y-%m-%d') THEN qty ELSE 0 END )-max( CASE b.ORDER_DATE WHEN date_format( DATE_ADD(#{theDate,jdbcType=DATE}, INTERVAL - 1 DAY), '%Y-%m-%d' ) THEN qty ELSE 0 END ))*100/ max( CASE b.ORDER_DATE WHEN date_format( DATE_ADD(#{theDate,jdbcType=DATE}, INTERVAL - 1 DAY), '%Y-%m-%d' ) THEN qty ELSE 0 END ),'2') AS hb FROM ( SELECT q.BRANCH_NO, q.ORDER_DATE, sum(q.qty) qty FROM ( SELECT o.BRANCH_NO, o.ORDER_DATE, i.MATNR, i.QTY FROM t_mst_inside_sal_order_item i JOIN t_mst_inside_sal_order o ON i.INS_ORDER_NO = o.INS_ORDER_NO AND i.ORDER_DATE BETWEEN DATE_ADD(#{theDate,jdbcType=DATE}, INTERVAL - 4 DAY) AND #{theDate,jdbcType=DATE} AND o.ORDER_DATE = i.ORDER_DATE JOIN t_md_branch b ON o.BRANCH_NO = b.BRANCH_NO
UNION ALL SELECT o.BRANCH_NO, o.ORDER_DATE, i.MATNR, i.CONFIRM_QTY qty FROM t_mst_disp_order_item i JOIN t_mst_disp_order o ON i.ORDER_NO = o.ORDER_NO AND i.ORDER_DATE BETWEEN DATE_ADD(#{theDate,jdbcType=DATE}, INTERVAL - 4 DAY) AND #{theDate,jdbcType=DATE} AND o.ORDER_DATE = i.ORDER_DATE AND o.`STATUS` = '20' JOIN t_md_branch b ON b.BRANCH_NO = o.BRANCH_NO
) q GROUP BY q.BRANCH_NO, q.ORDER_DATE ) b LEFT JOIN t_md_branch br ON b.BRANCH_NO = br.BRANCH_NO GROUP BY b.BRANCH_NO

SELECT BRANCH_NO, BRANCH_NAME FROM t_md_branch
SALES_ORG = #{salesOrg}
AND DEALER_NO = #{dealerNo}

SELECT z.BRANCH_NO branchNo, br.BRANCH_GROUP branchGroup, br.BRANCH_NAME branchName, z.qty initQty, y.qty validQty, cc.qty infoErrQty, k.qty custQty, q.qty superQty, qt.qty otherQty, y.qty / z.qty ratio FROM ( SELECT p.BRANCH_NO, sum(i.QTY) qty FROM T_MST_ORDER_DALIY_PLAN_ITEM i JOIN t_preorder p ON i.ORDER_NO = p.ORDER_NO AND i.DISP_DATE = DATE_FORMAT( DATE_ADD(#{theDate,jdbcType=DATE}, INTERVAL - 3 DAY), '%y-%m-%d' ) GROUP BY p.BRANCH_NO ) z LEFT JOIN ( SELECT p.BRANCH_NO, sum(i.QTY) qty FROM T_MST_ORDER_DALIY_PLAN_ITEM i JOIN t_preorder p ON i.ORDER_NO = p.ORDER_NO AND p.PREORDER_STAT = '10' AND i.`STATUS` != '30' AND i.DISP_DATE = #{theDate,jdbcType=DATE} GROUP BY p.BRANCH_NO ) y ON z.BRANCH_NO = y.BRANCH_NO LEFT JOIN ( SELECT p.BRANCH_NO, sum(i.QTY) qty FROM T_MST_ORDER_DALIY_PLAN_ITEM i JOIN t_preorder p ON i.ORDER_NO = p.ORDER_NO AND p.RET_REASON = '20' AND i.DISP_DATE = #{theDate,jdbcType=DATE} GROUP BY p.BRANCH_NO ) cc ON cc.BRANCH_NO = z.BRANCH_NO LEFT JOIN ( SELECT p.BRANCH_NO, sum(i.QTY) qty FROM T_MST_ORDER_DALIY_PLAN_ITEM i JOIN t_preorder p ON i.ORDER_NO = p.ORDER_NO AND p.RET_REASON = '30' AND i.DISP_DATE = #{theDate,jdbcType=DATE} GROUP BY p.BRANCH_NO ) k ON k.BRANCH_NO = z.BRANCH_NO LEFT JOIN ( SELECT p.BRANCH_NO, sum(i.QTY) qty FROM T_MST_ORDER_DALIY_PLAN_ITEM i JOIN t_preorder p ON i.ORDER_NO = p.ORDER_NO AND p.RET_REASON = '10' AND i.DISP_DATE = #{theDate,jdbcType=DATE} GROUP BY p.BRANCH_NO ) q ON q.BRANCH_NO = z.BRANCH_NO LEFT JOIN ( SELECT p.BRANCH_NO, sum(i.QTY) qty FROM T_MST_ORDER_DALIY_PLAN_ITEM i JOIN t_preorder p ON i.ORDER_NO = p.ORDER_NO AND p.RET_REASON = '40' AND i.DISP_DATE = #{theDate,jdbcType=DATE} GROUP BY p.BRANCH_NO ) qt ON qt.BRANCH_NO = z.BRANCH_NO JOIN t_md_branch br ON br.BRANCH_NO = z.BRANCH_NO
AND br.BRANCH_NO = #{branchNo}
AND br.DEALER_NO = #{dealerId}
AND br.SALES_ORG = #{salesOrg}

SELECT br.BRANCH_NAME, u.EMP_NAME, i.ORDER_DATE, i.REPLACE_REASON, v.VIP_NAME, v.MP, concat( r.RESIDENTIAL_AREA_TXT, a.ADDRESS_TXT ) ADDRESS_TXT, p.SHORT_TXT, i.QTY, ex.SHORT_TXT as CONFIRM_SHORT_TXT, i.CONFIRM_QTY
FROM t_mst_disp_order_item i LEFT JOIN t_preorder o ON o.ORDER_NO = i.ORG_ORDER_NO LEFT JOIN t_md_mara_ex p ON p.MATNR = i.MATNR AND p.SALES_ORG = o.SALES_ORG LEFT JOIN t_md_mara_ex ex on ex.MATNR = i .CONFIRM_MATNR and ex.SALES_ORG=o.SALES_ORG LEFT JOIN t_md_address a ON a.ADDRESS_ID = i.ADDRESS_NO LEFT JOIN t_vip_cust_info v ON v.VIP_CUST_NO = o.MILKMEMBER_NO LEFT JOIN t_md_residential_area r ON r.ID = a.RESIDENTIAL_AREA LEFT JOIN t_md_branch_emp u on u.EMP_NO=o.EMP_NO LEFT JOIN t_md_branch br on br.BRANCH_NO = o.BRANCH_NO LEFT JOIN t_mst_disp_order od on od.ORDER_NO=i.ORDER_NO WHERE i.REASON='10' and od.STATUS='20'
AND br.BRANCH_NO = #{branchNo}
AND br.DEALER_NO = #{dealerId}
AND br.SALES_ORG = #{salesOrg}
AND u.EMP_NO = #{empNo}
AND i.ORDER_DATE >= #{dateStart,jdbcType=DATE}
AND i.ORDER_DATE >= #{dateEnd,jdbcType=DATE}
order by i.ORDER_DATE desc

SELECT d.BRANCH_NO, br.BRANCH_NAME, e.EMP_NAME, d.EMP_NO, ( SELECT i.ITEM_NAME FROM t_sys_code_item i WHERE i.TYPE_CODE = AND i.ITEM_CODE = d.SPEC ) SPEC_NAME, IFNULL(sum(d.RECEIVE_NUM), 0) RECEIVE_NUM, IFNULL(sum(d.REAL_NUM), 0) REAL_NUM, ( IFNULL(sum(d.RECEIVE_NUM), 0) - IFNULL(sum(d.REAL_NUM), 0) ) AS dif FROM t_rec_bot_detail d LEFT JOIN t_md_branch br ON d.BRANCH_NO = br.BRANCH_NO LEFT JOIN t_md_dealer de ON br.DEALER_NO = de.DEALER_NO LEFT JOIN t_md_branch_emp e ON d.EMP_NO = e.EMP_NO WHERE 1 = 1
AND br.SALES_ORG = #{salesOrg}
AND br.BRANCH_NO = #{branchNo}
AND br.DEALER_NO = #{dealerId}
AND d.EMP_NO = #{empNo,jdbcType=VARCHAR}
AND d.CREATE_AT >= #{dateStart,jdbcType=DATE}
AND d.CREATE_AT >= #{dateEnd,jdbcType=DATE}
GROUP BY d.BRANCH_NO, e.EMP_NAME, SPEC_NAME ORDER BY d.BRANCH_NO, d.EMP_NO, d.SPEC

