mapper.select:
select

from t_ssm_stock
where BRANCH_NO = #{branchNo,jdbcType=VARCHAR}
and MATNR = #{matnr,jdbcType=VARCHAR}

mapper.delete:
delete from t_ssm_stock
where BRANCH_NO = #{branchNo,jdbcType=VARCHAR}
  and MATNR = #{matnr,jdbcType=VARCHAR}

mapper.update:
update t_ssm_stock
set UNIT = #{unit,jdbcType=VARCHAR},
  QTY = #{qty,jdbcType=DECIMAL}
where BRANCH_NO = #{branchNo,jdbcType=VARCHAR}
  and MATNR = #{matnr,jdbcType=VARCHAR}
