mapper.select:
SELECT p.MATNR, p.MATNR_TXT, p.SHORT_TXT, p.BASE_UNIT, p. STATUS, p.LAST_MODIFIED MARA_LAST_MODIFIED, p.CREATE_AT MARA_CREATE_AT, p.FIRST_CAT, (SELECT i.ITEM_NAME FROM t_sys_code_item I WHERE i.TYPE_CODE= AND i.ITEM_CODE=p.FIRST_CAT) FIRST_CAT_NAME, p.SEC_CATE, (SELECT i.ITEM_NAME FROM t_sys_code_item I WHERE i.TYPE_CODE= AND i.ITEM_CODE=p.SEC_CATE) SEC_CATE_NAME, p.BRAND, (SELECT i.ITEM_NAME FROM t_sys_code_item I WHERE i.TYPE_CODE= AND i.ITEM_CODE=p.BRAND) BRAND_NAME, p.WEIGHT, p.WEIGHT_UNIT, p.IMPORTANT_PRD_FLAG, p.SPEC, (SELECT i.ITEM_NAME FROM t_sys_code_item I WHERE i.TYPE_CODE= AND i.ITEM_CODE=p.SPEC) SPEC_NAME, p.SPEC_UNIT, p.BOT_SPEC, p.ZBOT_CODE, (SELECT i.ITEM_NAME FROM t_sys_code_item I WHERE i.TYPE_CODE= AND i.ITEM_CODE=p.ZBOT_CODE) ZBOT_CODE_NAME, p.SALES_ORG, p.DEALER_NO FROM t_md_mara_stud p where p.STATUS='Y' and p.SALES_ORG = #{salesOrg}
and (p.MATNR like '%${keyWord}%' or p.MATNR_TXT like '%${keyWord}%' or p.SHORT_TXT like '%${keyWord}%')
order by p.MATNR
