mapper.update:
update t_md_branch_emp set  LAST_MODIFIED = #{lastModified,jdbcType=TIMESTAMP},
  LAST_MODIFIED_BY = #{lastModifiedBy,jdbcType=VARCHAR},
  LAST_MODIFIED_BY_TXT = #{lastModifiedByTxt,jdbcType=VARCHAR},
  DEL_FLAG = #{delFlag}
where EMP_NO = #{empNo}

mapper.select:
  select emp.EMP_NO, emp.HR_EMP_NO, branch.BRANCH_NAME BRANCH_NO, emp.EMP_NAME, emp.EMP_TYPE, emp.TEL, emp.MP, emp.WEBCHAT, emp.SFZH,emp.DEL_FLAG 
from t_md_branch_emp emp,T_MD_BRANCH branch 
where emp.BRANCH_NO=branch.BRANCH_NO and (DEL_FLAG='N' or DEL_FLAG is null)

 		AND emp.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}


 	AND DEL_FLAG = #{delFlag,jdbcType=VARCHAR}
